import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import {
  Box,
  Button,
  Card,
  CardContent,
  TextField,
  InputAdornment,
  SvgIcon,
  makeStyles
} from '@material-ui/core';
import { Search as SearchIcon } from 'react-feather';
import Autocomplete from '@material-ui/lab/Autocomplete';

import data from './data';

const useStyles = makeStyles((theme) => ({
  root: {},
  importButton: {
    marginRight: theme.spacing(1)
  },
  exportButton: {
    marginRight: theme.spacing(1)
  }
}));
const API_URL = 'http://127.0.0.1:5000/items/ajax_data/';
const Toolbar = ({
  className, onChangeSearchField, basket, ...rest
}) => {
  const classes = useStyles();
  const items = data;
  const [allItems, setAllItems] = useState([]);

  useEffect(() => {
    if (allItems === undefined || allItems.length === 0) {
      fetch(API_URL)
        .then((res) => res.json())
        .then((json) => {
          setAllItems(json.data);
        });
    }
  });

  return (
    <div
      className={clsx(classes.root, className)}
      {...rest}
    >
      <Box mt={3}>
        <Card>
          <CardContent>
            <Box maxWidth={500}>
              <TextField
                fullWidth
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <SvgIcon
                        fontSize="small"
                        color="action"
                      >
                        <SearchIcon />
                      </SvgIcon>
                    </InputAdornment>
                  )
                }}
                placeholder="Search items"
                variant="outlined"
                onChange={(e) => onChangeSearchField(e)}
              />
            </Box>
            <Autocomplete
              id="combo-box-demo"
              options={allItems}
              getOptionLabel={(option) => option.title}
              style={{ width: 300 }}
              renderInput={(params) => <TextField {...params} label="Combo box" variant="outlined" />}
            />
            <Box
              display="flex"
              justifyContent="flex-end"
            >
              <Button
                color="primary"
                variant="contained"
              >
                Add item
              </Button>
            </Box>
          </CardContent>
        </Card>
      </Box>
    </div>
  );
};

Toolbar.propTypes = {
  className: PropTypes.string,
  onChangeSearchField: PropTypes.func,
  basket: PropTypes.array
};

export default Toolbar;
