import React, { useState } from 'react';
import {
  Box,
  Container,
  makeStyles
} from '@material-ui/core';
import axios from 'axios';

import Page from 'src/components/Page';
import Results from './Results';
import Toolbar from './Toolbar';
// import data from './data';

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.background.dark,
    minHeight: '100%',
    paddingBottom: theme.spacing(3),
    paddingTop: theme.spacing(3)
  }
}));

let items = [];
async function getData() {
  try {
    /* Retrieves all ITEMS data from DB */
    const res = await axios({
      url: 'http://127.0.0.1:5000/items/ajax_data/',
      method: 'get',
      timeout: 8000,
      headers: {
        'Content-Type': 'application/json',
      }
    });
    if (res.status === 200) {
      console.log(res.status);
    }
    console.log([...res.data.data]);
    return [...res.data.data];
  } catch (err) {
    console.error(err);
  }
  console.log('empty');
  return [];
}
items = getData();
const data = items;

const ItemsListView = () => {
  // state
  const classes = useStyles();
  const [items, setItems] = useState(data);
  const allItems = data;
  console.log(allItems);

  const onChangeSearchField = (e) => {
    /* Filters */
    const { value } = e.target;
    console.log(value);
    const test = allItems.filter((item) => {
      return item.name.toUpperCase().includes(e.target.value.toUpperCase());
    });
    setItems([...test]);
  };

  return (
    <Page
      className={classes.root}
      title="Items"
    >
      <Container maxWidth={false}>
        <Toolbar onChangeSearchField={onChangeSearchField} />
        <Box mt={3}>
          <Results items={items} />
        </Box>
      </Container>
    </Page>
  );
};

export default ItemsListView;
