import React, { useEffect, useState } from 'react';
import {
  Box,
  Container,
  makeStyles
} from '@material-ui/core';
// import axios from 'axios';

import Page from 'src/components/Page';
import Results from './Results';
import Toolbar from './Toolbar';
// import data from './data';

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.background.dark,
    minHeight: '100%',
    paddingBottom: theme.spacing(3),
    paddingTop: theme.spacing(3)
  }
}));

let search = false;

const ItemsListView = () => {
  // state
  const classes = useStyles();
  const [items, setItems] = useState([]);
  const [allItems, setAllItems] = useState([]);

  useEffect(() => {
    console.log(true);
    if (items === undefined || items.length === 0) {
      fetch('http://127.0.0.1:5000/items/ajax_data/')
        .then((res) => res.json())
        .then((json) => {
          setAllItems(json.data);
          if (search === false) {
            setItems(json.data);
          }
        });
    }
  });

  const onChangeSearchField = (e) => {
    /* Filters */
    search = true;
    const { value } = e.target;
    console.log(value);
    const test = allItems.filter((item) => {
      return item.name.toUpperCase().includes(e.target.value.toUpperCase());
    });
    setItems([...test]);
  };

  return (
    <Page
      className={classes.root}
      title="Items"
    >
      <Container maxWidth={false}>
        <Toolbar onChangeSearchField={onChangeSearchField} />
        <Box mt={3}>
          <Results items={items} />
        </Box>
      </Container>
    </Page>
  );
};

export default ItemsListView;
