import React, { useState } from 'react';
import {
  Box,
  Container,
  makeStyles
} from '@material-ui/core';
import axios from 'axios';

import Page from 'src/components/Page';
import Results from './Results';
import Toolbar from './Toolbar';
// import data from './data';

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.background.dark,
    minHeight: '100%',
    paddingBottom: theme.spacing(3),
    paddingTop: theme.spacing(3)
  }
}));

let data = [];

const ItemsListView = () => {
  // state
  const classes = useStyles();
  const [items, setItems] = useState(data);
  console.log(items);

  const getData = () => {
    /* Retrieves all ITEMS data from DB */
    axios.get('http://127.0.0.1:5000/items/ajax_data/').then((response) => {
      if (response.data && response.data.data.length > 0) {
        console.log(response.data.data[0]);
        data = [...response.data.data];
        setItems(data);
      }
    });
  };

  // if (items.length === 0) {
  //   console.log(items);
  //   getData();
  // }

  const onChangeSearchField = (e) => {
    /* Filters */
    const { value } = e.target;
    getData();
    const itemstest = items;
    console.log(value);
    const test = itemstest.filter((item) => {
      return item.name.toUpperCase().includes(e.target.value.toUpperCase());
    });
    console.log(test);
    data = test;
    setItems(test);
  };

  return (
    <Page
      className={classes.root}
      title="Items"
    >
      <Container maxWidth={false}>
        <Toolbar onChangeSearchField={onChangeSearchField} />
        <Box mt={3}>
          <Results items={items} />
        </Box>
      </Container>
    </Page>
  );
};

export default ItemsListView;
